<?phpnamespace Tupy\FileManager\Traits;use Illuminate\Database\Eloquent\Relations\MorphMany;use Tupy\FileManager\Models\FileManager;trait HasFiles{    /**     * @return MorphMany     */    public function files()    {        return $this->morphMany(FileManager::class, 'fileable');    }    /**     * @return mixed     */    public function profile()    {        return optional($this->files()->where('type', 'profile')->first());    }}