<?phpnamespace Tupy\FileManager\Traits;use Illuminate\Database\Eloquent\Relations\MorphMany;use Illuminate\Database\Eloquent\Relations\MorphOne;use Tupy\FileManager\Models\FileManager;trait HasFiles{    /**     * @return MorphMany     */    public function files(): MorphMany    {        return $this->morphMany(FileManager::class, 'fileable');    }    /**     * @return \Illuminate\Database\Eloquent\Relations\MorphOne     */    public function file(): MorphOne    {        return $this->morphOne(FileManager::class, 'fileable');    }    /**     * @param string $type     * @return \Illuminate\Support\Optional     */    public function profile($type = 'profile')    {        return optional($this->files()->where('type', '=', $type)->first());    }}